package com.example.config;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.Primary;import org.springframework.jdbc.datasource.DriverManagerDataSource;import javax.sql.DataSource;@Configurationpublic class DataSourceConfig {    @Primary    @Bean    @ConfigurationProperties(prefix = "spring.datasource")    public DataSource primaryDataSource() {        return new DriverManagerDataSource();    }    @Bean    @ConfigurationProperties("spring.second-datasource")    public DataSource secondDataSource() {        return new DriverManagerDataSource();    }}