package com.example.controller;import com.example.dto.ApiResponse;import com.example.dto.JwtDTO;import com.example.dto.ProfileDTO;import com.example.service.AuthService;import com.example.util.JWTUtil;import jakarta.servlet.http.HttpServletRequest;import jakarta.validation.Valid;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/v1/auth")public class AuthController {    @Autowired    private AuthService authService;    @PostMapping("/close/create")    public ResponseEntity<ApiResponse> create(@Valid @RequestBody ProfileDTO dto,                                              HttpServletRequest request) {        JwtDTO jwt = JWTUtil.decode(request);        return ResponseEntity.ok(authService.create(jwt, dto));    }}