package com.example.dto;import com.example.entity.ClientEntity;import com.example.enums.Status;import com.fasterxml.jackson.annotation.JsonInclude;import jakarta.validation.constraints.NotNull;import jakarta.validation.constraints.Size;import lombok.Data;@Data@JsonInclude(JsonInclude.Include.NON_NULL)public class ClientDTO extends BaseStringDTO {    @NotNull(message = "company Name should be not null!")    @Size(min = 3, message = "Name should be at least 3 characters")    private String companyName;    @NotNull(message = "login should be not null!")    @Size(min = 6, message = "login should be at least 6 characters")    private String login;    @NotNull(message = "password should be not null!")    @Size(min = 6, message = "password should be at least 6 characters")    private String password;    @NotNull(message = "status should be not null!")    private Status status;    @NotNull(message = "message should be not null!")    @Size(min = 3, message = "message should be at least 3 characters")    private String message;}