package com.example.dto;import com.fasterxml.jackson.annotation.JsonInclude;import jakarta.persistence.Column;import jakarta.validation.constraints.NotNull;import jakarta.validation.constraints.Pattern;import jakarta.validation.constraints.Size;import lombok.Data;@Data@JsonInclude(JsonInclude.Include.NON_NULL)public class ProfileDTO extends BaseStringDTO{    @Pattern(regexp = "^\\+?[1-9]\\d{7,14}$", message = "Invalid phone number format")    private String phone;    @NotNull(message = "message is required")    private String message;    private String text;}