package com.example.entity;import com.example.enums.Status;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Entity@Table(name = "profile")@Data@AllArgsConstructor@NoArgsConstructorpublic class ProfileEntity extends  BaseStringEntity {    @Column(name = "phone",            unique = true,            length = 13)    private String phone;    @Column(name = "message")    private String message;    @Column(name = "text")    private String text;}